create table cliente (id_cliente bigint not null, primer_apellido varchar(255), primer_nombre varchar(255), segundo_apellido varchar(255), segundo_nombre varchar(255), primary key (id_cliente));
create table tarjeta (numero bigint not null, activa boolean, bloqueada boolean, fecha_vencimiento date, saldo numeric(38,2), unicamente_dolar boolean, id_cliente bigint, primary key (numero));
create table tipo_producto (id_tipo_producto bigint not null, nombre_producto varchar(255), numero_producto bigint, primary key (id_tipo_producto));
create table tipo_transaccion (id_tipo_transaccion bigint not null, tipo varchar(255), primary key (id_tipo_transaccion));
create table transaccion (id_transaccion bigint not null, fecha_transaccion timestamp(6), habilitada boolean, valor numeric(38,2), id_tarjeta bigint, id_tipo_transaccion bigint, primary key (id_transaccion));
